mapper.select:
SELECT * FROM book_collection_book b
JOIN book ON b.book_id = book.id
WHERE b.collection_id = #{collectionId}
ORDER BY b.id
LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE book_collection
SET title = #{title}, content = #{content}, thumbnail = #{thumbnail}
WHERE id = #{id} AND user_id = #{userId}
